
def ext_gcd(a, b):
    if b == 0:
        return 1, 0
    else:
        x, y = ext_gcd(b, a % b)
        x, y = y, (x - (a // b) * y)
        return x, y

def modinv(a, m):
    x, y = ext_gcd(a, m)
    return x % m

def attack(c1, c2, e1, e2, n):
    # WRITE YOUR CODE HERE!
    s1, s2 = ext_gcd(e1, e2)
    if s1 < 0:
        s1 = - s1
        c1 = modinv(c1, n)
    elif s2 < 0:
        s2 = - s2
        c2 = modinv(c2, n)
    m = (pow(c1, s1, n) * pow(c2, s2, n)) % n
    return m


